mapper.select:

select  s.tno, s.seatno, s.issue ,sc.Scount,sc.rdate 
from seat s, (select * from schedule) sc 
where mno = #{mno} and s.issue = 0  and  to_date(sc.rdate, 'YYYY-MM-DD') = #{sc.rdate} 
order by  sc.rdate asc;

mapper.update:
update member
set pwd = #{pwd}, name = #{name} , birth = #{birth}, email= #{email}
where id = #{id}
